import React, { useEffect, useState} from 'react';
import NewsItem from './NewsItem';
import Loading from './Loading';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props)=> {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const capitalizeFirstLetter = (str)=> {
    return str.charAt(0).toUpperCase() + str.slice(1);
  }
  

  
  document.title = `${capitalizeFirstLetter(props.category)}-NewsWorld`;
  let imageNotFound = "data:image/png;base64,";
  

  

  const updateNews = async()=> {
    props.setProgress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;

    setLoading(true);
    let data = await fetch(url);
    props.setProgress(30);
    let parsedData = await data.json();

    props.setProgress(60);

    setArticles(parsedData.articles);
    setTotalResults(parsedData.totalResults);
    setLoading(false);

    props.setProgress(100);
  }
  
  useEffect(()=>{
    updateNews();
  },[])
  

  // handelPrevClick = async () => {
  //   this.setState({ page: .page - 1 });
  //   this.updateNews();
  // }
  // handelNextClick = async () => {
  //   this.setState({ page: .page + 1 });
  //   this.updateNews();
  // }

  const fetchMoreData = async () => {
    setPage(page+1);
    
    
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    
    setArticles(articles.concat(parsedData.articles));
    setTotalResults(parsedData.totalResults);
    setLoading(false);
  };
  


    return (
      <>

        <h1 className='text-center' style={{ margin: '40px 0px' }}>NewsWorld-Top {capitalizeFirstLetter(props.category)} Headlines</h1>
        {loading && <Loading />}
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          loader={<Loading />}
        >
          <div className='container'>
            <div className="row">
              {articles.map((element) => {
                return <div className="col-md-4" key={element.url}>
                  <NewsItem title={element.title ? element.title.slice(0, 70) : ""} description={element.description ? element.description.slice(0, 120) : ""} imageUrl={!element.urlToImage ? imageNotFound : element.urlToImage} readMore={element.url} author={!element.author ? "Unknown" : element.author} time={new Date(element.publishedAt).toGMTString()} source={element.source.name} />
                </div>
              })}
            </div>
          </div>
        </InfiniteScroll>
      </>
    )

}
            

News.defaultProps = {
  country: 'in',
  pageSize: 10,
  category: 'general'
}

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string
}

export default News
